style.css:
/*#region Colors */
/*#endregion*/
@import url('https://fonts.googleapis.com/css?family=Poppins:400,700,900&display=swap');
:host {
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
}
:host .container {
  margin: auto;
}
.container {
  font-family: Poppins, "Helvetica Neue", Helvetica, Arial, sans-serif;
  box-shadow: 0 11px 32px -5px rgba(54, 61, 77, 0.15);
}
@media (min-width: 960px) {
  .container {
    box-shadow: 0 10px 24px 0 rgba(54, 61, 77, 0.15);
  }
}
.title {
  font-weight: 900;
  font-size: 24px;
  line-height: 1.3333;
  margin-bottom: 24px;
}
@media (min-width: 960px) {
  .title {
    font-size: 28px;
    line-height: 1.2857;
  }
}
.description {
  font-size: 16px;
  margin-bottom: 24px;
}
.no-fields .form {
  margin-top: 8px;
}
.fields {
  margin-bottom: 32px;
}
.field + .field {
  margin: 24px 0 0 0;
}
.field-title {
  margin: 0 0 12px 0;
}
.radio .field-title,
.checkbox .field-title {
  margin: 0 0 16px 0;
}
.buttons {
  margin: -6px;
}
.buttons .button {
  margin: 6px;
  border-radius: 6px;
}
.button.secondary,
.button.secondary.icon {
  padding-left: 0;
  padding-right: 0;
  color: rgba(89, 67, 122, 0.3);
  background-color: transparent;
  font-weight: normal;
}
.button.secondary:hover,
.button.secondary.icon:hover,
.button.secondary:focus,
.button.secondary.icon:focus {
  color: rgba(89, 67, 122, 0.7);
  background-color: transparent;
}
.input-text,
.input-textarea,
.input-select {
  border: none;
  border-bottom: solid 1px rgba(89, 67, 122, 0.1);
  border-radius: 0;
  background-color: transparent;
}
.input-text::placeholder,
.input-textarea::placeholder,
.input-select::placeholder {
  color: rgba(89, 67, 122, 0.3);
}
.input-text {
  padding: 0 0 11px 0;
}
.input-textarea {
  padding: 0;
}
.input-select select {
  padding: 0 24px 11px 0;
}
.input-select:after {
  top: 8px;
  transform: none;
  right: 0;
}
.input-check label {
  margin-bottom: 20px;
  border-radius: 4px;
}
.input-check label span {
  padding-left: 36px;
}
.input-check label span:before {
  width: 24px;
  height: 24px;
  top: 0;
}
.input-check label input[type='radio'] + span:before,
.input-check label input[type='checkbox'] + span:before {
  background-color: transparent;
  border: 1px solid rgba(89, 67, 122, 0.1);
}
.input-check label input[type='radio'] + span:after,
.input-check label input[type='checkbox'] + span:after {
  left: 4px;
}
.input-check label input[type='radio']:checked + span:after,
.input-check label input[type='checkbox']:checked + span:after {
  left: 4px;
}
.input-rating label {
  width: 40px;
  height: 32px;
}
.note {
  margin: 32px 0 0 0;
  color: rgba(89, 67, 122, 0.3);
}
@keyframes scale-in-center {
  0% {
    transform: scale(0.5);
    opacity: 0;
  }
  100% {
    transform: scale(1);
    opacity: 1;
  }
}
:host(.enter) .container {
  animation: scale-in-center 0.4s cubic-bezier(0.39, 0.575, 0.565, 1) both;
}
:host(.leave) .container {
  animation: scale-in-center 0.4s cubic-bezier(0.39, 0.575, 0.565, 1) both reverse;
}
@keyframes shake {
  100% {
    transform: none;
  }
  0%,
  99% {
    transform: translate(0, 0);
  }
  20% {
    transform: translateX(-10px);
  }
  60% {
    transform: translateX(-5px);
  }
  40% {
    transform: translateX(10px);
  }
  80% {
    transform: translateX(5px);
  }
}
.form.submit .invalid {
  animation: shake 600ms;
}

/*#region Colors */
/*#endregion*/
.close,
.back {
  margin: 17px 17px 0 0;
}
@media screen and (min-width: 960px) {
  .close,
  .back {
    margin: 21px 21px 0 0;
  }
}
.container {
  border-radius: 0;
}
.content,
.description {
  font-size: 14px;
  line-height: 22px;
}
.content {
  align-items: center;
}
.description {
  width: 260px;
  margin-bottom: 32px;
}
.body {
  padding: 48px 26px 48px 26px;
  border: 1px solid #59437a;
  text-align: center;
}
@media screen and (min-width: 960px) {
  .body {
    padding: 86px 60px 86px 60px;
  }
}
.title {
  font-size: 32px;
  line-height: 40px;
}
@media screen and (min-width: 960px) {
  .title {
    font-size: 40px;
    line-height: 48px;
  }
}
.input-text {
  padding: 13px 16px;
  border: 1px solid rgba(89, 67, 122, 0.4);
}
@media (max-width: 960px) {
  .input-text {
    text-align: center;
  }
}
.input-text:hover {
  border-color: rgba(89, 67, 122, 0.6);
}
.input-text:focus {
  border-color: #59437a;
}
.fields {
  width: auto;
  flex-grow: 1;
  margin-bottom: 8px;
}
@media screen and (min-width: 960px) {
  .fields {
    margin: 0 12px 0 0;
  }
}
.content .form {
  width: 100%;
  margin: 0;
}
@media screen and (min-width: 960px) {
  .content .form {
    flex-direction: row;
    justify-content: center;
    align-items: flex-end;
  }
}
.content .form .button {
  flex-grow: 1;
  border-radius: 0;
  font-weight: 900;
  line-height: 22px;
  padding: 14px 27px 13px 27px;
}
@media screen and (min-width: 960px) {
  .content .form .button {
    flex-grow: 0;
  }
}
.content .form .button:hover,
.content .form .button:focus {
  text-decoration: underline;
  background-color: #59437a;
}
.content .form .secondary:hover,
.content .form .secondary:focus {
  background-color: transparent;
}
.content .form .button + .button {
  margin-top: 24px;
}
